"use strict";const vercelServerlessAPI=axios.create({baseURL:"https://weather-app-renv123.vercel.app/api/"});async function getCurrentWeatherDataForLocation(e){return(await doApiCall(`/weather-for-location?location=${e}`))?.data}async function getWeeklyWeatherDataForCoords(e,t){return(await doApiCall(`/weather-for-week?lat=${e}&lon=${t}`))?.data}async function getWeeklyWeatherDataForLocation(e){let t=await getCurrentWeatherDataForLocation(e),{lon:a,lat:r}=t?.coord;return{...await getWeeklyWeatherDataForCoords(r,a),name:t.name}}async function getAddressFromLatLng(e,t){return(await doApiCall(`/reverse-geocode?lat=${e}&lon=${t}`))?.data}async function getPictures(e,t=1){return(await doApiCall(`/picture?query=${e}&nr=${t}`))?.data?.results}async function doApiCall(e=isRequired()){let t;try{t=await vercelServerlessAPI.get(e)}catch(e){}return t}const isRequired=()=>{throw new Error("param is required")};export default{getCurrentWeatherDataForLocation:getCurrentWeatherDataForLocation,getWeeklyWeatherDataForCoords:getWeeklyWeatherDataForCoords,getWeeklyWeatherDataForLocation:getWeeklyWeatherDataForLocation,getAddressFromLatLng:getAddressFromLatLng,getPictures:getPictures};